PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE users (
	id INTEGER NOT NULL, 
	email VARCHAR NOT NULL, 
	hashed_password VARCHAR NOT NULL, 
	full_name VARCHAR, 
	role VARCHAR NOT NULL, 
	is_active BOOLEAN, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id)
);
CREATE TABLE soft_skills (
	id INTEGER NOT NULL, 
	name VARCHAR NOT NULL, 
	category VARCHAR(22) NOT NULL, 
	description TEXT, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id)
);
CREATE TABLE skill_questions (
	id INTEGER NOT NULL, 
	skill_id INTEGER NOT NULL, 
	question_text TEXT NOT NULL, 
	question_type VARCHAR, 
	is_active BOOLEAN, 
	order_index INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(skill_id) REFERENCES soft_skills (id)
);
CREATE TABLE skill_assessments (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	is_completed BOOLEAN, 
	started_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	completed_at DATETIME, 
	total_score INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE modules (
	id INTEGER NOT NULL, 
	title VARCHAR NOT NULL, 
	description TEXT, 
	"order" INTEGER, 
	course_id INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(course_id) REFERENCES courses (id)
);
CREATE TABLE question_options (
	id INTEGER NOT NULL, 
	question_id INTEGER NOT NULL, 
	option_text TEXT NOT NULL, 
	score_weight INTEGER, 
	order_index INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(question_id) REFERENCES skill_questions (id)
);
CREATE TABLE user_skill_assessments (
	id INTEGER NOT NULL, 
	assessment_id INTEGER NOT NULL, 
	skill_id INTEGER NOT NULL, 
	score INTEGER NOT NULL, 
	level VARCHAR(12) NOT NULL, 
	recommendations TEXT, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(assessment_id) REFERENCES skill_assessments (id), 
	FOREIGN KEY(skill_id) REFERENCES soft_skills (id)
);
CREATE TABLE lessons (
	id INTEGER NOT NULL, 
	title VARCHAR NOT NULL, 
	content TEXT, 
	"order" INTEGER, 
	module_id INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(module_id) REFERENCES modules (id)
);
CREATE TABLE user_answers (
	id INTEGER NOT NULL, 
	assessment_id INTEGER NOT NULL, 
	question_id INTEGER NOT NULL, 
	selected_option_id INTEGER, 
	text_answer TEXT, 
	scale_value INTEGER, 
	answered_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(assessment_id) REFERENCES skill_assessments (id), 
	FOREIGN KEY(question_id) REFERENCES skill_questions (id), 
	FOREIGN KEY(selected_option_id) REFERENCES question_options (id)
);
CREATE TABLE daily_tasks (
	id INTEGER NOT NULL, 
	title VARCHAR(200) NOT NULL, 
	description TEXT NOT NULL, 
	task_type VARCHAR(22) NOT NULL, 
	difficulty VARCHAR(6) NOT NULL, 
	points INTEGER NOT NULL, 
	is_active BOOLEAN, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id)
);
CREATE TABLE user_levels (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	total_points INTEGER, 
	current_level INTEGER, 
	points_to_next_level INTEGER, 
	PRIMARY KEY (id), 
	UNIQUE (user_id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE task_completions (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	task_id INTEGER NOT NULL, 
	completed_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	points_earned INTEGER NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(task_id) REFERENCES daily_tasks (id)
);
CREATE TABLE user_daily_task_assignments (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	task_id INTEGER NOT NULL, 
	assigned_date DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	is_completed BOOLEAN, 
	completed_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(task_id) REFERENCES daily_tasks (id)
);
CREATE TABLE learning_materials (
	id INTEGER NOT NULL, 
	title VARCHAR(255) NOT NULL, 
	description TEXT, 
	content_type VARCHAR(8) NOT NULL, 
	skill_type VARCHAR(22) NOT NULL, 
	difficulty_level VARCHAR(12) NOT NULL, 
	duration_minutes INTEGER, 
	author VARCHAR(100), 
	theory_content TEXT, 
	video_url VARCHAR(500), 
	video_id VARCHAR(100), 
	source_url VARCHAR(500), 
	is_published BOOLEAN, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id)
);
CREATE TABLE learning_tasks (
	id INTEGER NOT NULL, 
	material_id INTEGER NOT NULL, 
	title VARCHAR(255) NOT NULL, 
	description TEXT, 
	order_position INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id)
);
CREATE TABLE user_progress (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	course_id INTEGER, 
	completed_lessons INTEGER, 
	total_lessons INTEGER, 
	progress_percentage FLOAT, 
	last_accessed DATETIME, 
	total_time_spent INTEGER, 
	average_score FLOAT, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(course_id) REFERENCES courses (id)
);
CREATE TABLE material_progress (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	material_id INTEGER, 
	status VARCHAR(11), 
	completed_at DATETIME, 
	time_spent INTEGER, 
	experience_gained INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(material_id) REFERENCES learning_materials (id)
);
CREATE TABLE achievements (
	id INTEGER NOT NULL, 
	title VARCHAR NOT NULL, 
	description VARCHAR, 
	badge_image VARCHAR, 
	required_courses INTEGER, 
	required_lessons INTEGER, 
	required_score FLOAT, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id)
);
CREATE TABLE user_game_profiles (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	level INTEGER, 
	experience INTEGER, 
	total_points INTEGER, 
	courses_completed INTEGER, 
	lessons_completed INTEGER, 
	perfect_scores INTEGER, 
	login_streak INTEGER, 
	longest_streak INTEGER, 
	badges JSON, 
	titles JSON, 
	last_login DATETIME, 
	last_action DATETIME, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, experience_points INTEGER DEFAULT 0, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE rewards (
	id INTEGER NOT NULL, 
	title VARCHAR NOT NULL, 
	description VARCHAR, 
	reward_type VARCHAR, 
	image_url VARCHAR, 
	required_level INTEGER, 
	required_points INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id)
);
CREATE TABLE leaderboards (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	period VARCHAR, 
	score INTEGER, 
	rank INTEGER, 
	updated_at DATETIME, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE lesson_progress (
	id INTEGER NOT NULL, 
	user_progress_id INTEGER, 
	lesson_id INTEGER, 
	status VARCHAR(11), 
	score FLOAT, 
	time_spent INTEGER, 
	completed_at DATETIME, 
	notes VARCHAR, 
	last_position INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_progress_id) REFERENCES user_progress (id), 
	FOREIGN KEY(lesson_id) REFERENCES lessons (id)
);
CREATE TABLE user_achievements (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	achievement_id INTEGER, 
	achieved_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(achievement_id) REFERENCES achievements (id)
);
CREATE TABLE user_activities (
	id INTEGER NOT NULL, 
	profile_id INTEGER, 
	action_type VARCHAR(16), 
	points_earned INTEGER, 
	exp_earned INTEGER, 
	action_details JSON, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(profile_id) REFERENCES user_game_profiles (id)
);
CREATE TABLE user_rewards (
	id INTEGER NOT NULL, 
	profile_id INTEGER, 
	reward_id INTEGER, 
	acquired_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	is_equipped INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(profile_id) REFERENCES user_game_profiles (id), 
	FOREIGN KEY(reward_id) REFERENCES rewards (id)
);
CREATE TABLE quizzes (
	id INTEGER NOT NULL, 
	lesson_id INTEGER, 
	title VARCHAR NOT NULL, 
	description TEXT, 
	time_limit INTEGER, 
	passing_score FLOAT, 
	attempts_allowed INTEGER, 
	is_randomized BOOLEAN, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(lesson_id) REFERENCES lessons (id)
);
CREATE TABLE notifications (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	type VARCHAR(20), 
	title VARCHAR NOT NULL, 
	message TEXT NOT NULL, 
	data JSON, 
	is_read BOOLEAN, 
	is_email_sent BOOLEAN, 
	icon VARCHAR, 
	color VARCHAR, 
	action_url VARCHAR, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	read_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE notification_preferences (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	discussion_replies BOOLEAN, 
	solution_marked BOOLEAN, 
	course_updates BOOLEAN, 
	achievements BOOLEAN, 
	quiz_results BOOLEAN, 
	level_ups BOOLEAN, 
	reviews BOOLEAN, 
	course_completion BOOLEAN, 
	assignment_reminders BOOLEAN, 
	system_updates BOOLEAN, 
	email_notifications BOOLEAN, 
	push_notifications BOOLEAN, 
	email_digest_frequency VARCHAR, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE discussions (
	id INTEGER NOT NULL, 
	title VARCHAR NOT NULL, 
	content TEXT NOT NULL, 
	content_type VARCHAR(10), 
	content_id INTEGER, 
	author_id INTEGER, 
	is_pinned BOOLEAN, 
	is_answered BOOLEAN, 
	view_count INTEGER, 
	vote_count INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(author_id) REFERENCES users (id)
);
CREATE TABLE reviews (
	id INTEGER NOT NULL, 
	course_id INTEGER, 
	author_id INTEGER, 
	rating FLOAT NOT NULL, 
	content TEXT, 
	helpful_count INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(course_id) REFERENCES courses (id), 
	FOREIGN KEY(author_id) REFERENCES users (id)
);
CREATE TABLE questions (
	id INTEGER NOT NULL, 
	quiz_id INTEGER, 
	type VARCHAR, 
	text TEXT NOT NULL, 
	options JSON, 
	correct_answer JSON, 
	points FLOAT, 
	explanation TEXT, 
	code_template TEXT, 
	test_cases JSON, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(quiz_id) REFERENCES quizzes (id)
);
CREATE TABLE quiz_attempts (
	id INTEGER NOT NULL, 
	quiz_id INTEGER, 
	user_id INTEGER, 
	started_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	completed_at DATETIME, 
	score FLOAT, 
	max_score FLOAT, 
	passed BOOLEAN, 
	PRIMARY KEY (id), 
	FOREIGN KEY(quiz_id) REFERENCES quizzes (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE discussion_replies (
	id INTEGER NOT NULL, 
	content TEXT NOT NULL, 
	discussion_id INTEGER, 
	author_id INTEGER, 
	parent_id INTEGER, 
	is_solution BOOLEAN, 
	vote_count INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(discussion_id) REFERENCES discussions (id), 
	FOREIGN KEY(author_id) REFERENCES users (id), 
	FOREIGN KEY(parent_id) REFERENCES discussion_replies (id)
);
CREATE TABLE discussion_votes (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	discussion_id INTEGER, 
	value INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(discussion_id) REFERENCES discussions (id)
);
CREATE TABLE review_helpful (
	id INTEGER NOT NULL, 
	review_id INTEGER, 
	user_id INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(review_id) REFERENCES reviews (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE question_responses (
	id INTEGER NOT NULL, 
	attempt_id INTEGER, 
	question_id INTEGER, 
	answer JSON, 
	is_correct BOOLEAN, 
	points_earned FLOAT, 
	feedback TEXT, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(attempt_id) REFERENCES quiz_attempts (id), 
	FOREIGN KEY(question_id) REFERENCES questions (id)
);
CREATE TABLE reply_votes (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	reply_id INTEGER, 
	value INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(reply_id) REFERENCES discussion_replies (id)
);
CREATE TABLE courses (
	id INTEGER NOT NULL, 
	title VARCHAR NOT NULL, 
	description TEXT, 
	skill_type VARCHAR(22) NOT NULL, 
	difficulty_level VARCHAR(12) NOT NULL, 
	duration_hours INTEGER, 
	price FLOAT, 
	is_published BOOLEAN, 
	author_id INTEGER, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(author_id) REFERENCES users (id)
);
CREATE TABLE course_lessons (
	id INTEGER NOT NULL, 
	course_id INTEGER NOT NULL, 
	material_id INTEGER NOT NULL, 
	order_position INTEGER, 
	is_required BOOLEAN, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id), 
	FOREIGN KEY(course_id) REFERENCES courses (id), 
	FOREIGN KEY(material_id) REFERENCES learning_materials (id)
);
CREATE TABLE course_enrollments (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	course_id INTEGER NOT NULL, 
	enrolled_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	is_active BOOLEAN, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(course_id) REFERENCES courses (id)
);
CREATE TABLE course_exams (
	id INTEGER NOT NULL, 
	course_id INTEGER NOT NULL, 
	title VARCHAR NOT NULL, 
	description TEXT, 
	passing_score INTEGER, 
	time_limit_minutes INTEGER, 
	questions TEXT, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(course_id) REFERENCES courses (id)
);
CREATE TABLE course_completions (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	course_id INTEGER NOT NULL, 
	completed_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	final_score INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(course_id) REFERENCES courses (id)
);
CREATE TABLE exam_attempts (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	exam_id INTEGER NOT NULL, 
	answers TEXT, 
	score INTEGER, 
	passed BOOLEAN, 
	started_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	completed_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(exam_id) REFERENCES course_exams (id)
);
CREATE TABLE certificates (
	id INTEGER NOT NULL, 
	completion_id INTEGER NOT NULL, 
	certificate_number VARCHAR NOT NULL, 
	issued_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	template_data TEXT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(completion_id) REFERENCES course_completions (id), 
	UNIQUE (certificate_number)
);
CREATE INDEX ix_users_id ON users (id);
CREATE UNIQUE INDEX ix_users_email ON users (email);
CREATE INDEX ix_soft_skills_id ON soft_skills (id);
CREATE INDEX ix_skill_questions_id ON skill_questions (id);
CREATE INDEX ix_skill_assessments_id ON skill_assessments (id);
CREATE INDEX ix_modules_id ON modules (id);
CREATE INDEX ix_question_options_id ON question_options (id);
CREATE INDEX ix_user_skill_assessments_id ON user_skill_assessments (id);
CREATE INDEX ix_lessons_id ON lessons (id);
CREATE INDEX ix_user_answers_id ON user_answers (id);
CREATE INDEX ix_daily_tasks_id ON daily_tasks (id);
CREATE INDEX ix_user_levels_id ON user_levels (id);
CREATE INDEX ix_task_completions_id ON task_completions (id);
CREATE INDEX ix_user_daily_task_assignments_id ON user_daily_task_assignments (id);
CREATE INDEX ix_learning_materials_id ON learning_materials (id);
CREATE INDEX ix_learning_tasks_id ON learning_tasks (id);
CREATE INDEX ix_user_progress_id ON user_progress (id);
CREATE INDEX ix_material_progress_id ON material_progress (id);
CREATE INDEX ix_achievements_id ON achievements (id);
CREATE INDEX ix_user_game_profiles_id ON user_game_profiles (id);
CREATE INDEX ix_rewards_id ON rewards (id);
CREATE INDEX ix_leaderboards_id ON leaderboards (id);
CREATE INDEX ix_lesson_progress_id ON lesson_progress (id);
CREATE INDEX ix_user_achievements_id ON user_achievements (id);
CREATE INDEX ix_user_activities_id ON user_activities (id);
CREATE INDEX ix_user_rewards_id ON user_rewards (id);
CREATE INDEX ix_quizzes_id ON quizzes (id);
CREATE INDEX ix_notifications_id ON notifications (id);
CREATE INDEX ix_notification_preferences_id ON notification_preferences (id);
CREATE INDEX ix_discussions_id ON discussions (id);
CREATE INDEX ix_reviews_id ON reviews (id);
CREATE INDEX ix_questions_id ON questions (id);
CREATE INDEX ix_quiz_attempts_id ON quiz_attempts (id);
CREATE INDEX ix_discussion_replies_id ON discussion_replies (id);
CREATE INDEX ix_discussion_votes_id ON discussion_votes (id);
CREATE INDEX ix_review_helpful_id ON review_helpful (id);
CREATE INDEX ix_question_responses_id ON question_responses (id);
CREATE INDEX ix_reply_votes_id ON reply_votes (id);
CREATE INDEX ix_courses_title ON courses (title);
CREATE INDEX ix_courses_id ON courses (id);
CREATE INDEX ix_course_lessons_id ON course_lessons (id);
CREATE INDEX ix_course_enrollments_id ON course_enrollments (id);
CREATE INDEX ix_course_exams_id ON course_exams (id);
CREATE INDEX ix_course_completions_id ON course_completions (id);
CREATE INDEX ix_exam_attempts_id ON exam_attempts (id);
CREATE INDEX ix_certificates_id ON certificates (id);
COMMIT;
