INSERT INTO daily_tasks VALUES(1,'Поговорите с незнакомым человеком','Начните разговор с кем-то новым сегодня. Это может быть коллега из другого отдела, сосед или человек в очереди.','COMMUNICATION','EASY',15,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(2,'Попросите обратную связь','Попросите коллегу или руководителя дать вам обратную связь по недавней работе или проекту.','COMMUNICATION','MEDIUM',20,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(3,'Проведите презентацию','Представьте свою идею или проект перед группой людей, даже если это всего лишь 5-минутная презентация.','COMMUNICATION','HARD',30,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(4,'Активно слушайте собеседника','В следующем разговоре сосредоточьтесь на активном слушании - задавайте уточняющие вопросы и перефразируйте сказанное.','COMMUNICATION','EASY',10,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(5,'Возьмите инициативу в команде','Предложите новую идею или возьмите на себя организацию небольшого проекта или встречи.','LEADERSHIP','MEDIUM',25,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(6,'Помогите коллеге решить проблему','Найдите коллегу, у которого есть сложности, и предложите свою помощь в решении проблемы.','LEADERSHIP','EASY',15,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(7,'Дайте конструктивную обратную связь','Предоставьте полезную и конструктивную обратную связь коллеге или подчиненному.','LEADERSHIP','MEDIUM',20,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(8,'Организуйте командную встречу','Инициируйте и проведите короткую командную встречу для обсуждения текущих задач или планов.','LEADERSHIP','HARD',30,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(9,'Предложите помощь коллеге','Активно предложите помощь коллеге с его задачей или проектом.','TEAMWORK','EASY',10,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(10,'Организуйте совместный обед','Пригласите коллег на совместный обед или кофе-брейк для неформального общения.','TEAMWORK','EASY',15,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(11,'Поделитесь знаниями с командой','Проведите небольшую сессию знакомства с новым инструментом или методом работы для коллег.','TEAMWORK','MEDIUM',25,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(12,'Разрешите конфликт в команде','Если есть разногласия в команде, помогите найти компромисс и решение, которое устроит всех.','TEAMWORK','HARD',35,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(13,'Найдите альтернативное решение','Для любой задачи, с которой вы столкнетесь сегодня, найдите как минимум два разных способа ее решения.','PROBLEM_SOLVING','EASY',15,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(14,'Проанализируйте сложную проблему','Разбейте сложную проблему на более мелкие части и составьте план решения каждой из них.','PROBLEM_SOLVING','MEDIUM',20,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(15,'Используйте метод ''Пять почему''','Примените технику ''5 почему'' к любой проблеме, чтобы добраться до ее корня.','PROBLEM_SOLVING','MEDIUM',25,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(16,'Предложите инновационное решение','Найдите творческий и нестандартный подход к решению рабочей задачи.','PROBLEM_SOLVING','HARD',30,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(17,'Составьте детальный план дня','Распланируйте свой день по часам, включая время на задачи, перерывы и неожиданности.','TIME_MANAGEMENT','EASY',10,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(18,'Используйте технику Помодоро','Поработайте минимум 2 часа, используя технику Помодоро (25 минут работы, 5 минут перерыв).','TIME_MANAGEMENT','EASY',15,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(19,'Устраните одного пожирателя времени','Определите одну вещь, которая тратит ваше время попусту, и исключите ее сегодня.','TIME_MANAGEMENT','MEDIUM',20,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(20,'Делегируйте задачу','Найдите задачу, которую можно делегировать, и передайте ее подходящему человеку с четкими инструкциями.','TIME_MANAGEMENT','HARD',25,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(21,'Проанализируйте свои эмоции','В течение дня 3 раза остановитесь и запишите, что вы чувствуете и почему.','EMOTIONAL_INTELLIGENCE','EASY',10,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(22,'Проявите эмпатию к коллеге','Попытайтесь понять точку зрения коллеги, с которым у вас есть разногласия.','EMOTIONAL_INTELLIGENCE','MEDIUM',20,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(23,'Управляйте стрессом','Когда почувствуете стресс, примените технику дыхания или медитации для успокоения.','EMOTIONAL_INTELLIGENCE','MEDIUM',25,1,'2025-06-01 13:11:59');
INSERT INTO daily_tasks VALUES(24,'Поддержите коллегу','Найдите коллегу, который выглядит расстроенным или подавленным, и предложите поддержку.','EMOTIONAL_INTELLIGENCE','EASY',15,1,'2025-06-01 13:11:59');
